import './MapStyle.css';
import React, {useEffect, useRef, useState} from 'react';
import 'ol/ol.css';
import Map from 'ol/Map';
import View from 'ol/View';
import 'ol/ol.css';
import OSM from 'ol/source/OSM';
import {Group, Tile as TileLayer} from 'ol/layer';
import {ImageWMS} from "ol/source";
import {Overlay} from "ol";

function BaseMap() {
    const [map, setMap] = useState();
    const mapElement = useRef();
    const mapRef = useRef();
    mapRef.current = map;


    const container = document.getElementById('popup');

    const base = new TileLayer({
        source: new OSM(),
    });

    const view = new View({
        projection: 'EPSG:4326',
        center: [78.0, 23.0],
        zoom: 5,

    });

    const overlay = new Overlay({
        element: container,
        autoPan: true,
        autoPanAnimation: {
            duration: 250
        }
    });

    const base_maps = new Group({
        'title': 'Base maps',
        layers: [
            new TileLayer({
                title: 'OSM',
                type: 'base',
                visible: true,
                source: new OSM()
            })
        ]
    });


    const overlays = new Group({
        'title': 'Overlays',
        layers: [
            new Image({
                title: 'aus1',
                // extent: [-180, -90, -180, 90],
                source: new ImageWMS({
                    url: 'http://localhost:8080/geoserver/wms',
                    params: {'LAYERS': 'aussie:aus1'},
                    ratio: 1,
                    serverType: 'geoserver'
                })
            }),
            new Image({
                title: 'aus2',
                // extent: [-180, -90, -180, 90],
                source: new ImageWMS({
                    url: 'http://localhost:8080/geoserver/wms',
                    params: {'LAYERS': 'aussie:aus2'},
                    ratio: 1,
                    serverType: 'geoserver'
                })
            }),
            new Image({
                title: 'aus3',
                // extent: [-180, -90, -180, 90],
                source: new ImageWMS({
                    url: 'http://localhost:8080/geoserver/wms',
                    params: {'LAYERS': 'aussie:aus3'},
                    ratio: 1,
                    serverType: 'geoserver'
                })
            })

        ]
    });


    useEffect(() => {
        const initialMap = new Map({
            target: mapElement.current,
            view: view,
            overlays: [overlay]
        });
        setMap(initialMap)
        initialMap.addLayer(base_maps);
        initialMap.addLayer(overlays);
    }, []);
    return (
        <div ref={mapElement} className="map-container"/>
    );
}

export default BaseMap;
