import './MapStyle.css';
import React, {useEffect, useRef, useState} from 'react';
import 'ol/ol.css';
//import TileWMS from 'ol/source/TileWMS';
import Map from 'ol/Map';
import View from 'ol/View';
import 'ol/ol.css';
import OSM from 'ol/source/OSM';
import {Tile as TileLayer} from 'ol/layer';
import {ImageWMS} from "ol/source";
import {Group} from "ol/layer";
import {Image} from "ol/layer"
import VectorSource from "ol/source/Vector";
import {GeoJSON} from "ol/format";
import VectorLayer from "ol/layer/Vector";
import {Stroke, Style} from "ol/style";

const base = new Group({
    'title': 'Base maps',
    layers: [
        new TileLayer({
            title: 'OSM',
            type: 'base',
            visible: true,
            source: new OSM()
        })
    ]
});
const vectorSource = new VectorSource({
    format: new GeoJSON(),
    url: 'http://localhost:8080/topp/geoserver/wfs?service=WFS&' +
        'version=1.1.0&request=GetFeature&typename=topp:states&' +
        'outputFormat=application/json&srsname=EPSG:4326&' +
        'bbox=24.9,-124.8,49.5,-66.0',
});
/*
http://localhost:8080/geoserver/wfs?service=WFS&version=1.1.0&request=GetFeature$typename=topp:states&outputFormat=application/json&srsname=EPSG:4326&layers=topp%3Astates&bbox=-124.73142200000001,24.955967,-66.969849,49.371735

const vector = new VectorLayer({
    source: vectorSource,
    style: new Style({
        stroke: new Stroke({
            color: 'rgba(0, 0, 0, 1.0)',
            width: 2,
        }),
    }),
});

/*
const overlays = new Group({
    'title': 'Overlays',
    layers: [
        new Image({
            title: 'countries',
            visible: true,
            source: new ImageWMS({
                url: 'http://localhost:8080/geoserver/wms',
                params: {'LAYERS': 'quaketext:quake_text'},
                ratio: 1,
                serverType: 'geoserver'
            })
        })
    ]
});
*/

function BaseMap() {
    const [map, setMap] = useState();
    const mapElement = useRef();
    const mapRef = useRef();
    mapRef.current = map;

    useEffect(() => {
        const initialMap = new Map({
            target: mapElement.current,
            layers: [base, vector],
            view: new View({
                projection: 'EPSG:4326',
                center: [78.0, 23.0],
                zoom: 2,
                minZoom: 2,
                maxZoom: 18,
            }),
        });
        setMap(initialMap)
    }, []);
    return (
        <div ref={mapElement} className="map-container"/>
    );
}

export default BaseMap;
